  import { vpInput } from '@VUID_COMPONENTS_PUBLIC';

  export default {
    components: {
      vpInput,
    },
    data: {
      //  type属性表示input的类型，maxLength限制input的输入长度。
      value:  '12323',
      placeholder: '请输入内容',
      disabled: false, // 是否禁止输入
      key: null, // 当hasKey为String或Number时，会改变onChange的返回参数的类型。
